package com.learning.javatutorial;

public class TrainingContinue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int num = 1;
			int input = 3;
			String star = "*";
			
			while (num <= input) {
					System.out.println(star);
					star = star + "*"; //
					num++;
			}
	}

}
